import React, { Suspense, useState } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { action_fetchSingleProduct } from '../../actions/get-products';
import heart from "../../assests/images/heart.svg";
import Pagination from '../common/pagination/Pagination';
import { Link } from 'react-router-dom';

let PageSize = 10;


function ProductDisplay() {
    const dispatch = useDispatch();

    const [currentPage, setCurrentPage] = useState(1);

    const productList = useSelector((state: any) => state.getProductList.getProductList);

    const onProductClick = (e: any) => {
        // <Link to="product-detail">Click to view our about page</Link>
        console.log(e, "e" , e.target.__reactProps$5rfpdnq90mw.id)
        dispatch(action_fetchSingleProduct(2))
    }


    return (
        <div className='product-display-container'>
            <Suspense fallback={<div>Loading</div>}>
                <div className="aem-Grid aem-Grid--12">
                    {productList && productList.map(function (key: any) {
                        return (
                            <div className="aem-GridColumn aem-GridColumn--default--4">
                                <div>
                                    <img
                                        className='product-img'
                                        src={key.image}
                                        id={key.id}
                                        onClick={onProductClick}
                                    >
                                    </img>
                                </div>
                                <div>
                                    <label>{key.title}</label>
                                </div>
                                <div>
                                    <label>{key.price}</label>
                                </div>
                                <div>
                                    <img src={heart}></img>
                                </div>
                            </div>
                        );
                    })}
                </div>
                <div className="aem-Grid aem-Grid--12">
                    <Pagination
                        className="pagination-bar"
                        currentPage={currentPage}
                        totalCount={productList ? 20 : 20}
                        pageSize={PageSize}
                        onPageChange={(page: any) => setCurrentPage(page)}
                    />
                </div>
            </Suspense>
        </div>
    );
}

export default ProductDisplay;
