import React, { useState, useEffect } from "react";
import pluscircle from "../../assests/images/pluscircle.svg";
import minuscircle from "../../assests/images/minuscircle.svg";
import { useDispatch, useSelector } from "react-redux";
import { action_setQuantity, action_setTotalPrice } from "../../actions/get-products";

// quntity selection
function Quantity(props: any) {
    const dispatch = useDispatch();

    //local state
    const [quantity, setquantity] = useState(1);
    const [quantityCart, setquantityCart] = useState(1);

    //redux state
    const setQuantity = useSelector((state: any) => state.getProductList.setQuantity);

    const quantityItem = window.localStorage.getItem('quantity');
    let quantityValue = quantityItem ? JSON.parse(quantityItem) : setQuantity

    //maintain state on refresh 
    const Product = window.localStorage.getItem('cart');
    let uniqueCartItemList = Product ? JSON.parse(Product) : [];

    useEffect(() => {
        let totol = 0;
        uniqueCartItemList && uniqueCartItemList.map((key: any) => {
            totol = (key.price * quantity) + totol;
            console.log("setQuantity", setQuantity, totol, (key.price * setQuantity))
            window.localStorage.setItem('total', JSON.stringify(totol))
            dispatch(action_setTotalPrice(totol));
        })
    }, [quantity]);

    //on decrement
    const onChangeDecrement = (e: any) => {
        if (quantity > 1) {
            if (props.quantityId !== 0) {
                setquantity(quantity - 1)
                dispatch(action_setQuantity(quantity.toString()));
            } else {
                setquantityCart(quantityCart - 1)
                dispatch(action_setQuantity(quantityCart.toString()));
            }
        }
        window.localStorage.setItem('quantity', JSON.stringify(quantity))
    }

    //on increment
    const onChangeIncrement = (e: any) => {
        window.localStorage.setItem('quantity', JSON.stringify(e.target.id))
        if (props.quantityId !== 0) {
            setquantity(quantity + 1)
            dispatch(action_setQuantity(quantity.toString()));
        } else {
            setquantityCart(quantityCart + 1)
            dispatch(action_setQuantity(quantityCart.toString()));
        }
        window.localStorage.setItem('quantity', JSON.stringify(quantity))

    }

    console.log(quantity, setQuantity, quantityValue)
    //retun component
    return (
        <div className="quantity-container">
            {
                <div>
                    <span >{<img id={props.quantityId} className="quantity-logo-minus" alt="decrement" src={minuscircle} onClick={onChangeDecrement}></img>
                    }</span>

                    <span id={props.quantityId} className="quantity-span">{(props.quantityId !== 0 ? quantity : quantityCart)}</span>

                    <span>{<img id={props.quantityId} className="quantity-logo-plus" alt='increment' src={pluscircle} onClick={onChangeIncrement}></img>
                    }</span>

                </div>
            }
        </div>
    );
}

export default Quantity;
