import React, { useState, useEffect } from "react";
import pluscircle from "../../assests/images/pluscircle.svg";
import minuscircle from "../../assests/images/minuscircle.svg";
import { useDispatch, useSelector } from "react-redux";
import { action_setQuantity } from "../../actions/get-products";

// quntity selection
function Quantity(props: any) {
    const dispatch = useDispatch();

    //local state
    const [quantity, setquantity] = useState(1);
     
    //redux state
    const productDetail = useSelector((state: any) => state.getProductList.singleProductDetail);
    const setQuantity = useSelector((state: any) => state.getProductList.setQuantity);
    const Product = window.localStorage.getItem('data');
    const singleProductDetail = (Object.keys(productDetail).length > 0) ? productDetail : (Product ? JSON.parse(Product) : {})


    //on decrement
    const onChangeDecrement = () => {
        if (quantity > 1) {
            setquantity(quantity - 1)
            dispatch(action_setQuantity(quantity.toString()));
        }
    }

    //on increment
    const onChangeIncrement = () => {
        setquantity(quantity + 1);
        dispatch(action_setQuantity(quantity.toString()));
    }

    //retun component
    return (
        <div className="quantity-container">
            <span>{<img className="quantity-logo-minus" alt="decrement" src={minuscircle} onClick={onChangeDecrement}></img>
            }</span>

            <span className="quantity-span">{setQuantity}</span>

            <span>{<img className="quantity-logo-plus" alt='increment' src={pluscircle} onClick={onChangeIncrement}></img>
            }</span>
        </div>
    );
}

export default Quantity;
