  return (
    <>
      <div>
        <Suspense fallback={<h1>Still Loadingâ€¦</h1>}>
          <header>
            <Appbar />
          </header>
          <main>
            {/* 1,159 */}
            <Routes>
              <Route path={ROUTES.PATH_HOME} element={<ProductList />} />
              <Route path={ROUTES.PRODUCT_DETAILS} element={<ProductDetail />} />
              {/* <Route path={ROUTES.SHOPPING_CART} element={<ShoppingCart />} /> */}
            </Routes>
          </main>
          <footer>
            <Footer />
          </footer>
        </Suspense>
      </div>
    </>
  );
}

export default App;
