import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { IMG_CONST_VALUE } from '../../constant/common';

// single product display
function SingleProductDisplay() {

    // redux state
    const ProductDetail = useSelector((state: any) => state.getProductList.singleProductDetail);

    const [singleProductDetail, setsingleProductDetail] = useState(ProductDetail);


    useEffect(() => {
        const data = window.localStorage.getItem('data');
        if (data != null) {
            setsingleProductDetail(JSON.parse(data))
        }
    }, [])

    useEffect(() => {
        window.localStorage.setItem('data', JSON.stringify(ProductDetail))
    }, [singleProductDetail])

    console.log(singleProductDetail, "singleProductDetail")

    // return component
    return (
        <div>
            <div className="aem-Grid aem-Grid--12">
                <div className='aem-GridColumn aem-GridColumn--default--3 aem-GridColumn--phone--hide'>
                    {
                        <div>
                            {
                                IMG_CONST_VALUE.map((key) => {
                                    return (
                                        <div className='side-product-div'>
                                            <img className='side-product-display' src={singleProductDetail.image}>
                                            </img>
                                        </div>
                                    )
                                })

                            }
                        </div>
                    }
                </div>
                <div className='aem-GridColumn aem-GridColumn--default--9 aem-GridColumn--phone--12'>
                    <div>
                        <img className='product-display' src={singleProductDetail.image}>
                        </img>
                    </div>
                </div>
            </div>
            <div className="aem-Grid aem-Grid--12">
                <div>
                    <label className='product-title-span'>
                        {singleProductDetail.title}
                    </label>
                </div>
                <div>
                    <label className='product-discription-span'>
                        Description
                    </label>
                </div>
                <div>
                    <p>
                        {singleProductDetail.description}
                    </p>
                </div>
            </div>

            <div className='end-border'>

            </div>
        </div>
    );
}

export default SingleProductDisplay;
