import { Box } from '@mui/material';
import React, { useState } from "react";

function Quantity() {
    let status 
    const [quantity, setquantity] = useState('');

    const [quantity1, setquantity1] = useState(quantity);

    const onChangeDecrement = () => {
        if (parseInt(quantity) > 1) {
            setquantity(parseInt(quantity) - 1)
        }
        setquantity1(quantity)
    }

    const onChangeIncrement = () => {
        setquantity(quantity + 1)
        
        setquantity1(quantity)
    }

    console.log("quantity1",quantity1 ,quantity)
    return (
        <div>
            <div>
                <Box className="box" component="span" sx={{ p: 1, border: '1px solid #91959C' }}>
                    <label
                        onClick={onChangeDecrement}
                    >
                        -
                    </label>
                </Box>
                <Box className="box" component="span" sx={{ p: 1, border: '1px solid #91959C' }}>
                    <label>{quantity1}</label>
                </Box>
                <Box className="box" component="span" sx={{ p: 1, border: '1px solid #91959C' }}>
                    <label onClick={onChangeIncrement}>+</label>
                </Box>
            </div>
        </div>
    );
}

export default Quantity;
