import React, { useState, useEffect } from "react";
import pluscircle from "../../assests/images/pluscircle.svg";
import minuscircle from "../../assests/images/minuscircle.svg";
import { useDispatch, useSelector } from "react-redux";
import { action_setQuantity } from "../../actions/get-products";

// quntity selection
function Quantity(props: any) {
    const dispatch = useDispatch();

    //local state
    const [quantity, setquantity] = useState(1);

    //redux state
    const setQuantity = useSelector((state: any) => state.getProductList.setQuantity);

    //maintain state on refresh 
    const Product = window.localStorage.getItem('quantity');
    let qunatityValue = Product ? JSON.parse(Product) : setQuantity;

    
    const Pr = window.localStorage.getItem('quantity-value');
    let qunatity0 = Pr ? JSON.parse(Pr) : setQuantity;


    console.log("setQuantity",setQuantity)
    //on decrement
    const onChangeDecrement = (e: any) => {
        window.localStorage.setItem('quantity', JSON.stringify(e.target.id))
        if (quantity > 1) {
            setquantity(quantity - 1)
            window.localStorage.setItem('quantity-value', JSON.stringify(quantity))
            dispatch(action_setQuantity(quantity.toString()));
        }
    }

    //on increment
    const onChangeIncrement = (e: any) => {
        window.localStorage.setItem('quantity', JSON.stringify(e.target.id))
        setquantity(quantity + 1);
        window.localStorage.setItem('quantity-value', JSON.stringify(quantity))
        dispatch(action_setQuantity(quantity.toString()));
    }

    console.log(props.quantityId === parseInt(qunatityValue), parseInt(qunatityValue), props.quantityId)
    //retun component
    return (
        <div className="quantity-container">
            {
                // uniqueCartItemList && uniqueCartItemList.map((key: any) => {
                //     return (
                <div>
                    <span >{<img id={props.quantityId} className="quantity-logo-minus" alt="decrement" src={minuscircle} onClick={onChangeDecrement}></img>
                    }</span>

                    <span id={props.quantityId} className="quantity-span">{(props.quantityId === parseInt(qunatityValue)) ? setQuantity : 1}</span>

                    <span>{<img id={props.quantityId} className="quantity-logo-plus" alt='increment' src={pluscircle} onClick={onChangeIncrement}></img>
                    }</span>

                </div>
                //     )
                // })
            }
        </div>
    );
}

export default Quantity;
