// import ProductList from "../container/ProductList";
import ProductDetail from "../container/ProductDetail";
import ShoppingCart from "../container/ShoppingCart";
import Appbar from "../component/header/Appbar"
import Footer from "../component/footer/Footer";
import { Routes, Route } from "react-router-dom";
import { ROUTES } from "../constant/routes";
import { lazy, Suspense } from "react";

const ProductList = lazy(() => import('../container/ProductList'))

function App() {
  const dispatch = useDispatch();

  const [currentPage, setCurrentPage] = useState(1);

  // if user gets authenticated then only call for user details
  useEffect(() => {
    dispatch(action_fetchProductList());
    // dispatch(action_fetchProductByCategory('jewelery'));
    //dispatch(action_fetchSingleProduct(1));
    //dispatch(action_sortProductPrice('desc'));
  }, []);


  return (
    <>
      <div>
        <Suspense fallback={<h1>Still Loadingâ€¦</h1>}>
          <header>
            <Appbar />
          </header>
          <main>
            {/* 1,159 */}
            <Routes>
              <Route path={ROUTES.PATH_HOME} element={<ProductList />} />
              <Route path={ROUTES.PRODUCT_DETAILS} element={<ProductDetail />} />
              <Route path={ROUTES.SHOPPING_CART} element={<ShoppingCart />} />
            </Routes>
          </main>
          <footer>
            <Footer />
          </footer>
        </Suspense>
      </div>
    </>
  );
}

export default App;
